import Vue from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)
export default new Vuex.Store({
  modules: (r => {
    let result = {}
    r.keys().forEach(key => {
      let item = r(key).default
      result[item.name] = item
    })
    return result
  })(require.context('../../../../src/', true, /store\.js$/))
})
